import { useState } from 'react'

const ShowState = (props) => {
    return (
      <div>{props.text} {props.state}</div>
    )
}

const Button = (props) => {
  return (
      <button onClick = {props.handleClick}>{props.text}</button>
  )
}

const Keskiarvo = (props) => {

    let keskiarvo = (props.taulukko[0]*1+props.taulukko[2]*(-1)) / (props.taulukko[0] +props.taulukko[1] + props.taulukko[2])

    return (
      <div>{props.text} {keskiarvo}</div>
    )
}

const Kaikki = (props) => {
    return (
      <div>{props.text} {props.all[0]+props.all[1]+props.all[2]}</div>
    )
}

const Positive = (props) => {
  let positive = (props.taulukko[0] /(props.taulukko[0] +props.taulukko[1] + props.taulukko[2]))*100
  return (
    <div>{props.text} {positive} %</div>
  )
}

const StatisticLine = (props) => {
  return ( 
    <div>{props.text} {props.value}</div>
  )
}

const Statistics = (props) => {
  const good = props.taulukko[0]
  const neutral = props.taulukko[1]
  const bad = props.taulukko[2]
  if ((good + neutral+ bad) == 0) {
    return (
      <div>
        <h2>
          statistics
        </h2>
        No feedback given
      </div>
    )
  }
  return(
    <div>
      <h2>
        statistics
      </h2>
      <StatisticLine text ={"good"} value ={good}></StatisticLine>
      <StatisticLine text ={"neutral"} value ={neutral}></StatisticLine>
      <StatisticLine text ={"bad"} value ={bad}></StatisticLine>

      <StatisticLine text = {"all"} value = {good+neutral+bad}></StatisticLine>
      <StatisticLine text ={"average"} value = {(good*1+bad*(-1)) / (good +neutral + bad)}></StatisticLine>
      <StatisticLine text = {"positive"} value ={(props.taulukko[0] /(props.taulukko[0] +props.taulukko[1] + props.taulukko[2]))*100}><p>%</p></StatisticLine>


    </div>
  )
}

const App = () => {
  // tallenna napit omaan tilaansa
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)

  const goodClick = () => {
    setGood(good + 1)
  }
  const neutralClick = () => setNeutral(neutral +1)
  const badClick = () => setBad(bad +1)

  return (
    <div>
      <h2>give feedback</h2>
      <Button handleClick ={goodClick} text={"good"}></Button>
      <Button handleClick ={neutralClick} text={"neutral"}></Button>
      <Button handleClick ={badClick} text={"bad"}></Button>
      <Statistics taulukko ={[good,neutral,bad]}></Statistics>
    </div>
  )
}

export default App
