import { useState, useEffect } from 'react'
import axios from 'axios'


const Filter = (props) => {
  return (
    <div>
      filter shown with:<input value={props.filter} onChange = {props.funktio}/>
    </div>

  )
}

const Persons = (props) => {
  const persons = props.persons
  const newFilter = props.filter
  return ( 
    <div>
      {persons.filter((henkilo) => henkilo.name.toLowerCase().includes(newFilter.toLowerCase())).map((person)=> <p key={person.name}>{person.name} {person.number}</p>)}
    </div>
  )
}

const PersonForm = (props) => {

  return (
  <form onSubmit={props.lisaaNimiFunktio}>
    <div>
      name: <input value={props.nimi} onChange={props.funktioNimi}/>
    </div>
    <div>
      number: <input value={props.numero} onChange={props.funktioNumero}/>
    </div>
    <div>
      <button type="submit">add</button>
    </div>
  </form>
  )
}

const App = () => {
  const [persons, setPersons] = useState([])
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [newFilter, setFilter] = useState('')

  useEffect(() => {
    axios
    .get('http://localhost:3001/persons')
    .then(vastaus => {
      setPersons(vastaus.data)
    })
  },[])




  const lisaaNimi = (event) => {
    event.preventDefault()

    if (persons.find(olio => olio.name === newName)) {
      alert(`${newName} is already added to phonebook`)
      return
    }

    const ihminen = {
      name: newName,
      number: newNumber
    }

    setPersons(persons.concat({ihminen}))

  }


  return (
    <div>
      <h2>Phonebook</h2>
      <div>
        <Filter funktio= {e => setFilter(e.target.value)} filter ={newFilter}></Filter>
      </div>
      <h3>add a new</h3>
        <PersonForm 
        nimi={newName}
        numero={newNumber}
        funktioNumero={e => setNewNumber(e.target.value)}
        funktioNimi = {e => setNewName(e.target.value)}
        lisaaNimiFunktio={lisaaNimi}
         ></PersonForm>
      <h2>Numbers</h2>
        <Persons persons = {persons} filter = {newFilter}></Persons>
    </div>
  )

}

export default App
